import React from 'react';
import { useState } from 'react';
import './App.css';
import Cards from './views/Cards.jsx';
import Nav from './components/Nav';
import axios from 'axios';
import {Routes, Route} from 'react-router-dom';
import About from './views/About';
import Detail from './views/Detail';
import Error404 from './views/Error404';


function App() {
   const [characters, setCharacters] = useState([]);

   //repetido lo podría vvvvv  hacer con un .find()
   function repetido (objs, num) {
      let includes = true;
      if(objs.length === 0) return true;
      objs.forEach( el => {
         console.log(el.id, Number(num))
         if (el.id === Number(num)){
            includes = false;
         };
      });
      return includes;
   }

   function onSearch(id) {
      if(!repetido(characters, id)) return window.alert('¡Ya hay personajes con este ID!!!!!'); 
      if(id > 826 || id < 1) return window.alert('¡No hay personajes con este ID!!!!!');
      axios(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('¡No hay personajes con este ID!');
         }
      });
   };

   function onClose(id) {
      let newChar = characters.filter (el => el.id !== id);
      setCharacters(newChar);
   }
   return (
      <div className='App'>
         <Nav 
            onSearch={onSearch}
         />
         <Routes>
            <Route exact path="/" element={<About/>}/>
            <Route exact path="/home" element={<Cards
               characters={characters}
               onClose={onClose}/>}/>
            <Route exact path="/about" element={<About/>}/>
            <Route exact path="/detail/:id" element={<Detail/>}/>
            {/* acá arriba va el :id xq el id lo pasa el elemento Detail */}
            <Route path="*" element={<Error404/>}/>
         </Routes>

         {/* <Cards
            characters={characters}
            onClose={onClose}
         /> */}

      </div>
   );
}

export default App;
