import { ADD_FAV, FILTER, ORDER, REMOVE_FAV } from "./action";

const initialState = {
  myFavorites: [],
  allCharacters: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAV:
      return {
        ...state,
        allCharacters: [...state.allCharacters, action.payload],
        myFavorites: [...state.allCharacters, action.payload]
      };
      case REMOVE_FAV:
      return {
        ...state,
        allCharacters: state.allCharacters.filter(el => el.id !== action.payload)
      }
      case FILTER:
      return {
        ...state,
        myFavorites: state.allCharacters.filter(el => el.gender === action.payload)
      }
      case ORDER:
        const newCha = [...state.allCharacters];
        if(action.payload === 'A') {
          return {
            ...state,
            /* myFavorites: state.allCharacters.sort((a, b) => a.id - b.id) */
            myFavorites: newCha.sort((a, b) => a.id - b.id)
          }
        } else {
          return {
            ...state,
            /* myFavorites: state.allCharacters.sort((a, b) => b.id - a.id) */
            myFavorites: newCha.sort((a, b) => b.id - a.id)
          }
        }
      default:
        return {
          ...state
        }
  }
};

export default rootReducer;
