let emailRegExp = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
passwordRegExp = /^(?=.*\d)(?=.*[a-zA-Z]).{6,10}$/i;

/*
passwordRegExp = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/gm;
-at least 8 characters
- must contain at least 1 uppercase letter, 1 lowercase letter, and 1 number
- Can contain special characters
 */

export default function validation (obj) {
  let errors = {};
  if(obj.email === ''){
    errors.email = 'Se requiere un nombre';
  }
  if(obj.email.length > 0 && !emailRegExp.test(obj.email)) {
    errors.email = 'Debe ser un correo electrónico'
  }
  if(obj.email && obj.email.length > 36) {
    errors.email = 'Mas de 36 characters'
  }
  if(obj.password === ''){
    errors.password = 'Se requiere un password';
  }
  if(obj.email.length > 0 && !passwordRegExp.test(obj.password)) {
    errors.password = 'La contraseña tiene que tener al menos un número y una longitud entre 6 y 10 caracteres'
  }
  return errors;
};
